-- справочник категорий растений
create table if not exists reference.categories (
	id int generated by default as identity primary key,
	title varchar(512) not null,
	description text null
);

-- column comments
comment on column reference.categories.id is 'category identifier';
comment on column reference.categories.title is 'name category';
comment on column reference.categories.description is 'category description';

-- insert base values
,(2,'Садовые растения', 'садоводство  отрасль растениеводства, занимающаяся: возделыванием многолетних плодовых или ягодных культур (плодоводство); выращиванием декоративных растений (декоративное садоводство)')
,(3,'Срезочные растения', 'срезанные и поставленные в вазы с водой цветочно-декоративные растения. в качестве примера срезочных культур по сезонам приводим следующие: ранней весной — подснежник, нарцисс, ландыш, душистая фиалка, незабудки, анемон, ранункул; несколько позже — пеон, лилии; летом — душистый горошек, резеда, левкой, гвоздика голландская, гранатная и венская; осенью — астры однолетние и многолетние, гвоздика голландская (шабо и маргарита), георгины, гладиолус, монбреция, рудбекия, флокс многолетний, пиретрум гибридум, солидаго, хризантемы.')
,(4,'Огородные растения', 'огородных растений очень много, и принадлежат они к разным семействам, а объединяет их то, что они чаще других растений используются в пищу. при распределении культур на огороде следует придерживаться таблицы совместимости, разработанной специалистами: так можно предотвратить быстрое распространение вредителей и заболеваний, избежать конкуренции между растениями в вопросе питания и, соответственно, повысить урожаи. источник: https://floristics.info/ru/metki/ogorodnye.html')
on conflict do nothing;


-- справочник растений
create table if not exists reference.plants (
	id int generated by default as identity primary key, -- идентификтаор, первичный ключ растений
	title varchar(512) not null, -- наименование растения
    category_id int not null, -- указатель  на категорию растения
    short_info jsonb not null default '{}'::jsonb, -- набор кратких данных о растении
    notes jsonb not null default '{}'::jsonb, -- набор фактов пр растение
    img_links jsonb not null default '{}'::jsonb, -- массив ссылок на изображения растения
    creator varchar(128) not null default current_user, -- пользователь, создавший запись
    created_at timestamptz not null default current_timestamp, -- дата-время создания записи
    modifier varchar(128) null, -- пользователь, внесший последние изменения
    modified_at timestamptz null, -- дата-время последних изменений
    constraint plants_category_id_categories_fk foreign key (category_id) references reference.categories(id) on update cascade on delete cascade
);

comment on column reference.plants.id is 'plant identifier';
comment on column reference.plants.title is 'plant name';
comment on column reference.plants.category_id is 'reference to categories';
comment on column reference.plants.short_info is 'short reference about plant';
comment on column reference.plants.notes is 'details about plant';
comment on column reference.plants.img_links is 'array of the links to plant images';
comment on column reference.plants.created_at is 'when record created';
comment on column reference.plants.creator is 'user, who created record';
comment on column reference.plants.modified_at is 'when record last updated';
comment on column reference.plants.modifier is 'user, who last updated record';

