INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Арбуз (Citrullus)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"засухоустойчивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды"}'::jsonb, '[{"title":"Виды и сорта арбуза","content":"Арбуз обыкновенный (Citrúllus lanátus)"},{"title":"Уход за арбузом","content":"Арбуз — свето- и теплолюбивая культура, плохо переносящая любое затенение. Для нормального развития растений необходима температура +25…+30 °С, для завязи — не ниже +18…+25 °С."},{"title":"Посадка арбуза","content":"Для выращивания арбузов рекомендуется использовать рассадный метод. Масса 1000 семян — 60-150 г. Семена сохраняют всхожесть в течение 4-5 лет. Семена высевают на глубину 1 см по одному в горшочки, заполненные почвой на две трети, остальную почву добавляют постепенно на протяжении всего периода выращивания рассады."},{"title":"Выращивание арбуза в закрытом грунте (в теплицах)","content":"Урожай арбузов можно получить, выращивая их в средней полосе под пленочным укрытием."},{"title":"Исторические факты об арбузах","content":"Арбуз был известен еще 4 тыс. лет назад в Египте. Его изображения найдены на древнеегипетских гробницах, в которых также обнаружены его семена и остатки листьев. Из Египта арбуз попал в Аравию, Палестину, Сирию, а затем в Среднюю Азию."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Базилик, рейган (Ocimum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"листья, побеги"}'::jsonb, '[{"title":"Выращивание базилика","content":"Базилик можно выращивать в течение всего лета на солнечном, хорошо защищенном патио вместе с другими контейнерными растениями. На затененном участке резко падает урожай зеленой массы и ее ароматичность. Посадив базилик в защищенном уголке сада, на окне, выходящем на южную сторону, или в теплице на плодородной почве среди сладкого перца, баклажанов, помидоров, огурцов, что будет способствовать хорошему росту этих растений, отпугнет вредителей и украсит стол прекрасной приправой для многих блюд."},{"title":"Сорта базилика","content":"Очень элегантно в любом саду будет смотреться красновато-пурпурный базилик с зубчатыми листьями (O. Basilicum minimum) сорт Red Ruffles, или базилик с переливчатой пурпурной окраской."},{"title":"Применение базилика в медицине","content":"Базилик – очень древнее растение. Его выращивали в Древней Греции, Индии. Приятный, с легкой горчинкой вкус базилика, способствовал быстрому распространению этого растения по странам мира. В народе его называли «травой долгожителей»."},{"title":"Болезни и вредители базилика","content":"Песчаный медляк"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Баклажан, бадриджан (Solanum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое, умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды"}'::jsonb, '[{"title":"Виды и сорта баклажана","content":"Баклажан, или паслен темноплодный (Solanum melongena)"},{"title":"Уход за баклажаном","content":"Баклажан является одной из самых требовательных к условиям выращивания культур. Оптимальная температура для его роста и развития — +25…+28 °C. Он чувствителен к колебаниям температуры: понижение или сильное ее повышение вызывает опадание бутонов, цветков и завязей. В открытом грунте эту культуру выращивают на Северном Кавказе и в Нижнем Поволжье, в центральных районах страны баклажан встречается как в открытом, так и в защищенном грунте, а в нечерноземье культуру возделывают редко."},{"title":"Посадка баклажана","content":"Баклажаны выращивают рассадным способом. Для выращивания рассады на подоконнике лучше подойдут окна с южной и восточ­ной стороны."},{"title":"Секреты успеха в выращивании баклажана","content":"1. Баклажан выращивают только через рассаду. Рассада готова к высадке в возрасте 45-60 дней."},{"title":"Полезные свойства баклажана","content":"В составе плодов баклажана есть вода, жиры, белки, углеводы, клетчатка, пектины, соланин, каротин, витамины С, Р, РР, В1, В2, существенное количество витамина В9, никотиновая и фолиевая кислоты; и минеральные вещества: калий, медь, кальций, фосфор, магний и железо. В кожуре баклажана много фенольных соединений, являющимися антиоксидантами."},{"title":"Посев баклажана по Лунному календарю","content":"Наиболее благоприятные дни для посева баклажана по Лунному календарю приходятся на вторую четверть Луны (Растущая Луна) в знаках Рака, Скорпиона, Весов, Рыб и Овна."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Горох (Pisum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"плоды"}'::jsonb, '[{"title":"Виды и сорта гороха","content":"Однолетние травянистые растения со слабыми лазящими стеблями, цепляющимися за опору с помощью усиков. Листья перистые. Цветки белые или фиолетовые, мотылькового типа. Корневая система мощная."},{"title":"Полезные свойства гороха","content":"Продукты из гороха являются одними из самых калорийных и питательных. В зерне содержится большое количество пищевых волокон, углеводов, насыщенных жирных кислот, витаминов и микроэлементов. Горох является незаменимым источником растительного белка. Горох содержит большое количество антиоксидантов, стимулирует работу кишечника, снимает изжогу."},{"title":"Популярные сорта гороха:","content":"‘Авола’ – лущильный, раннеспелый, бобы среднего размера с 6-9 семенами;"},{"title":"Уход за горохом","content":"Горох — довольно привередливая культура, он не любит жару, относительно холодостоек."},{"title":"Посадка гороха","content":"Лучшими предшественниками для гороха являются тыквенные и пасленовые культуры: картофель, капуста, огурцы и тыква. Не следует размещать горох после других зернобобовых культур. Сам же горох является хорошим предшественником для большинства овощных культур (кроме фасоли, бобов, сои)."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Кабачок (Cucurbita)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое, умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды, цветы"}'::jsonb, '[{"title":"Виды и сорта кабачков","content":"Кабачок (Cucurbita pepo var. Giromontina)"},{"title":"Уход за кабачками","content":"Кабачки - одна из наиболее холодостойких культур из семейства тыквенных. Кабачок хорошо растет и развивается при температурах +15…+20 °С, однако оптимальная температура — +20…+25 °С. Кабачки переносят кратковременное похолодание до +6 °С, но не переносит заморозков."},{"title":"Посадка кабачков","content":"При выращивании кабачка на грядах почву перед посевом перекапывают на глубину 25–27 см, не допуская выворачивания подпахотного неплодородного слоя."},{"title":"Применение кабачка в медицине","content":"Семена кабачка содержат сапонины, применяемые в медицине для лечения многих заболеваний."},{"title":"Посев кабачков по Лунному календарю","content":"Наиболее благоприятные дни для посева кабачков по Лунному календарю приходятся на вторую четверть Луны (Растущая Луна) в знаках Весы, Рак, Скорпион, Телец и Рыбы."},{"title":"Болезни и вредители кабачков","content":"Бактериальная гниль"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Капуста белокочанная (Brassica)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"теневыносливое, светолюбивое","regard_to_moisture":"влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"листья"}'::jsonb, '[{"title":"Виды и сорта капусты белокочанной","content":"Капуста белокочанная, капуста огородная (Brássica olerácea)"},{"title":"Уход за капустой белокочанной","content":"Капуста довольно холодостойкая овощная культура, она плохо переносит жару (оптимальная температура + 15…+20 °С)."},{"title":"Посадка капусты белокочанной","content":"Капусту выращивают рассадным способом. Рассада капусты довольно холодостойка, переносит понижение температуры до –3 °С."},{"title":"Болезни и вредители капусты","content":"Успешное выращивание капусты белокочанной частично зависит от профилактических мероприятий и эффективной защиты от болезней и вредителей."},{"title":"Применение в медицине","content":"Капуста — старинное лечебное средство, которое высоко ценили еще в Древней Греции, Риме и конечно особо капусту ценили на Руси. Так известно, что измельченная свежая капуста и ее сок обладают эффективным антибактериальным действием. Рассол квашеной капусты улучшает пищеварение, способствует отделению желчи, оказывает мягкое слабительное действие. Свежий и высушенный экстракт белокочанной капусты эффективен при лечении язвенной болезни желудка и двенадцатиперстной кишки, энтерита, колита кожных заболеваний, кардиосклероза."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Капуста брокколи (Brassica)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"теневыносливое, светолюбивое","regard_to_moisture":"влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"цветы"}'::jsonb, '[{"title":"Виды и сорта капусты брокколи","content":"Брокколи (Brassica oleracea var. italica)"},{"title":"Уход за брокколи","content":"Брокколи — сравнительно холодостойкая культура (оптимальная температура +16…+25 °С), позднеспелые сорта выдерживают заморозки до -7...-10 °С."},{"title":"Посадка брокколи","content":"Брокколи можно выращивать рассадным способом и непосредственно высевать в грунт."},{"title":"Исторический факт о брокколи","content":"Брокколи в культуре выращивают с древних времен (примерно 2 тыс. лет), и была она известна еще у римлян, однако в Северной Америке ее «признали» только в начале ΧΧ века."},{"title":"Полезные свойства брокколи","content":"Брокколи – диетический, легкоусвояемый продукт питания, богатый ценными питательными веществами. Брокколи по питательности превосходит цветную капусту - в ней в 1,5 раза больше белков, в 2 раза больше минеральных солей, почти в 2 раза больше витамина С. Брокколи богата клетчаткой, что помогает работе кишечника. Содержащиеся в ней антиоксиданты сохраняют молодость кожи и защищают ее от вредного воздействия окружающей среды."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Капуста краснокачанная (Brassica)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"теневыносливое, светолюбивое","regard_to_moisture":"влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"листья"}'::jsonb, '[{"title":"Условия содержания краснокочанной капусты","content":"Капуста краснокочанная – двулетнее растение и является культурой длинного дня. Именно летом, когда длина световой части дня максимальная (15-17 часов), происходит активное развитие и рост листовой розетки."},{"title":"Сорта краснокочанной капусты, рекомендуемые Агрофирмой ПОИСК:","content":"Победа - Среднеспелый сорт (период от высадки рассады до уборки кочанов 105-110 дней). Розетка средних размеров (50-60 см). Кочан округло-плоский, темно-фиолетовый, на разрезе - фиолетовый. Внутренняя кочерыга кочанов средней длины. Масса кочана во время уборке 1,3-1,5 кг и более, плотные и устойчивы к растрескиванию. Вкусовые качества в свежем виде отличные."},{"title":"Уборка урожая краснокочанной капусты","content":"Урожай краснокочанной капусты собирают по мере формирования кочанов. Перед закладкой на хранение в погреб их очищают до плотно прилегающих листьев и оставляют наружную кочерыгу длинной 2-4 см. Кочаны краснокочанной капусты, сохраняются до весны."},{"title":"Болезни и вредители краснокочанной капусты","content":"Болезни и вредители и методы борьбы с ними такие же, как и у капусты белокочанной."},{"title":"Полезные свойства краснокочанной капусты","content":"Вкус этой капусты специфический, ярко выраженный капустный, и более приятный, чем у белокочанной. Краснокочанная капуста отличается от белокочанной не только окраской, но и химическим составом. Она содержит больше витамина С (почти в два раза) и белка, и при хранении не теряет полезных свойств."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Капуста цветная ( Brassica )','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"цветы"}'::jsonb, '[{"title":"Виды и сорта цветной капусты","content":"Капуста цветная (Brassica oleracea var. botrytis)"},{"title":"Уход за цветной капустой","content":"Как и все капусты, цветная капуста — довольно холодостойкая культура, переносит небольшие понижения температуры до -1…-2 °С."},{"title":"Посадка цветной капусты","content":"Цветную капусту выращивают рассадным способом."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Картофель, картошка (Solanum tuberosum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"клубни"}'::jsonb, '[{"title":"Виды и сорта картофеля","content":"Картофель — многолетнее растение с травянистыми стеблями. Цветет белыми, розовыми или фиолетовыми цветками. Плод — ядовитая зеленая ягода диаметром до 2 см с большим количеством мелких семян."},{"title":"","content":"На свету под кожурой клубней накапливаются гликоалкалоиды (соланин и чаконин), которые могут вызвать отравление человека, при варке они частично растворяются в воде."},{"title":"Классификация сортов картофеля","content":"По назначению:"},{"title":"Популярные столовые сорта картофеля","content":"‘Бронницкий’ — среднеспелый, клубень овальный, кожура гладкая, мякоть светло-желтая. Масса клубня 88-120 г, содержание крахмала 12,3-15,5%. Вкус удовлетворительный и хороший."},{"title":"Уход за картофелем","content":"Картофель хорошо растет в условиях умеренного климата на плодородных почвах среднего увлажнения."},{"title":"Посадка картофеля","content":"Клубни картофеля к посадке начинают готовить с осени. Отобранные клубни необходимо озеленить, это придаст им устойчивость против гнилостных бактерий. Срок озеленения 8–15 дней."},{"title":"Уборка урожая картофеля","content":"Ранние сорта картофеля на еду (в качестве молодого картофеля) убирают по зеленой ботве во время начала цветения картофеля. На семена и на зимнее употребление убирают, когда ботва начинает отмирать."},{"title":"Посадка картофеля по Лунному календарю","content":"Наиболее благоприятные дни для посадки картофеля по Лунному календарю приходятся на третью четверть Луны (Убывающая Луна) в знаках Козерог, Весы, Телец, Рак и Скорпион."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Кинза, кориандр (Coriandrum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"теневыносливое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"плоды, листья"}'::jsonb, '[{"title":"Виды и сорта кинзы","content":"Кориандр, кинза, киндза, клоповник, колендра, кишнец (Coriandrum sativum)"},{"title":"Уход за кинзой","content":"Кориандр — холодостойкая культура. Лучший период выращивания зелени – весна и осень, летом растения быстрее развиваются, но сформировав листовую розетку так же быстро переходят к цветению."},{"title":"Применение кинзы в медицине и кулинарии","content":"Молодые листья кориандра богаты витамином С, каротином, рутином, витаминами B1 и В2. В плодах содержится от 0,5 до 1% эфирных масел, в процессе длительного хранения семена теряют их содержание"},{"title":"Болезни и вредители кориандра","content":"Кориандр поражает ржавчина и мучнистая роса. Меры борьбы – ведение правильного севооборота, сжигание растительных остатков, а также глубокая перепашка участка."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Лук репчатый (Allium)','4', '{"kind":"двулетник, однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"цветы, листья, луковицы"}'::jsonb, '[{"title":"Виды и сорта лука","content":"Лук репчатый, или Лук севок (Allium cepa)"},{"title":"Уход за луком","content":"Лук — светолюбивая и холодостойкая культура. В период роста листьев требовательная к влажности почвы. В период вызревания луковиц поливы стоит прекратить, как минимум за 20 дней до уборки."},{"title":"Уборка урожая лука","content":"Когда луковицы сформируются, их ложный стебель размягчается, и у части растений листья полегают. Не следует ждать полегания всех растений, убирать урожай лучше при ясной солнечной погоде. Лук после уборки урожая необходимо просушить в течении 10 дней, после обрезать и сложить в ящики или корзины."},{"title":"Посадка лука","content":"В средней полосе России, в Сибири и на Дальнем Востоке лук выращивают через севок. В первый год из семян получают мелкие луковицы (севок), а во второй год из севка выращивают крупные луковицы."},{"title":"Выращивание репчатого лука из севка","content":"Севок высаживают в открытый грунт с 25 апреля по 5 мая с расстоянием между рядами 15–25 см, а в ряду 10–12 см (можно 15х15 см). После посадки лук проливают водой и сверху закрывают пленкой. Во время роста лук один раз подкармливают раствором мочевины (1 столовая ложка на 10 л воды)."},{"title":"Болезни и вредители репчатого лука","content":"Пероноспороз (ложная мучнистая роса)"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Морковь (Daucus)','4', '{"kind":"двулетник, однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимостойкое","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"корни"}'::jsonb, '[{"title":"Виды и сорта моркови","content":"Морковь посевная или морковь культурная (Daucus sativus, Daucus carota subsp. sativus)"},{"title":"Посадка и уход моркови","content":"Один из главных секретов в выращивании морковки заключается в ее поливе. Он должен быть своевременным и в меру обильным. Морковь лучше растет, когда земля все время поддерживается во влажном состоянии. В связи с этим в последние годы разработали новую технологию ее выращивания: летняя засуха часто не позволяет вырастить нормальную морковь в рядках, поэтому предпочтительно сажать ее гнездами и мульчировать. В течение вегетационного сезона проводят обязательный полив в лунки или методом-дождевания, избегая резких перепадов влажности почвы. Если за неделю вашего отсутствия земля сильно высохла, то поливайте морковь в два этапа: вечером небольшой подготовительный полив, а утром — основной."},{"title":"","content":"Почва и удобрения для моркови"},{"title":"Почва и удобрения для моркови","content":"Для нормального развития корнеплодов необходима почва с глубоким пахотным слоем. Морковь хорошо растет на достаточно рыхлой, супесчаной или легкой суглинистой плодородной почве с высоким содержанием гумуса и хорошим воздушно-газовым режимом. Тяжелые суглинистые и глинистые почвы непригодны для выращивания моркови. При выращивании на плотной почве у корнеплодов развиваются чечевички, которые, разрастаясь, придают им уродливый вид."},{"title":"Уборка урожая моркови","content":"Время уборки урожая приходит, когда ботва у моркови желтеет, это легко проверить, выдернув корнеплод. Поздние сорта моркови убирают, как правило, в сентябре. При уборке корнеплоды осторожно подкапывают и оставляют сушиться на грядке. Листья срезают, оставляя хвостики длиной около 1 см."},{"title":"Посадка моркови","content":"Для получения длинных корнеплодов почву за 1-2 месяца до посева перекапывают на глубину 10 см и хорошо проливают."},{"title":"Подзимний посев моркови","content":"Чтобы получить раннюю продукцию моркови применяют подзимние посевы. Подзимний посев надо проводить в сухую погоду при стабильных заморозках, перед устойчивым похолоданием в слегка подмерзшую землю в заранее подготовленные бороздки. В конце октября – начале ноября семена сеют на глубину 1-2 см и мульчируют посевы сухим торфом или перегноем слоем в 3-4 см. Особенно хорошие подзимние посевы получаются на легких почвах."},{"title":"Посев моркови по Лунному календарю","content":"Наиболее благоприятное время для посева моркови по Лунному календарю приходится на третью четверть Луны (Убывающая Луна) в знаках Козерог, Рак, Скорпион, Весы, Телец."},{"title":"Болезни и вредители моркови","content":"Серая гниль"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Огурец, огурцы (Cucumis sativus)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды"}'::jsonb, '[{"title":"Классификация сортов огурцов","content":"Немного подробнее разберемся с основными группами сортов огурцов, информация о которых часто указывается на упаковках."},{"title":"Посадка огурцов","content":"Грядку для выращивания огурцов в открытом грунте готовят в виде слоеного пирога: верхний слой почвы сначала снимается, на всю площадь предполагаемой гряды (или только вдоль предполагаемых рядов) насыпают порубленный хворост, можно хвойный лапник, прошлогодний (не совсем перепревший) компост, торф, солома или опилки, на этот настил — небольшой слой навоза, или перегноя с добавлением золы, а сверху — около 20-25 см огородной земли. Весь этот органический «компот» будет постепенно перепревать и выделять тепло, а через некоторое время превратится в отличное удобрение."},{"title":"Уход за огурцами","content":"Как и другие «жильцы» огорода, огурец требует к себе постоянного внимания. Эта культура не из тех, про которые можно без проблем забыть на неделю. Такое невнимание, конечно, не смертельно, но обязательно скажется на урожае."},{"title":"","content":"Посев огурцов по Лунному календарю"},{"title":"Посев огурцов по Лунному календарю","content":"Наиболее благоприятные дни для посева огурцов по Лунному календарю приходятся на первую четверть Луны (Растущая Луна) в знаках Рака, Весов, Скорпиона, Тельца и Рыб."},{"title":"Еще на эту тему:","content":"Тепло, светло и сыро — что еще огурцу нужно? Выращивать огурцы можно по-разному: хотите стандартную грядку, и ваши площади позволяют — пожалуйста, просто высаживайте, не хотите гнуть спину, или участок очень маленький — выращивайте на вертикальной шпалере... Поговорим о сексе: секреты опыления огурцов Многие огородники считают, что огурчики, получившиеся в результате «полноценного растительного секса» наиболее вкусные..."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Патиссон (Cucurbita)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое, умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды, цветы"}'::jsonb, '[{"title":"Виды и сорта патиссона","content":"Корневая система стержневая и мощная, обладает большой сосущей силой. Листья крупные на длинных черешках и имеют часто жесткое опушение. За счет их большого числа и размера, растение обладает высокой ассимиляционной активностью и отдача плодов происходит длительный период. Цветки крупные, желтого цвета и располагаются в пазухах листьев. Цветки мужские и женские располагаются на одном растении – это указывает на однодомность растения. Мужские цветки располагаются группами, а женские – одиночно. У патиссонов цветение наступает позднее чем на 10-12 дней. Плоды готовые к употреблению готовы через 7-12 суток после опыления. Зрелые плоды имеют срок сохранности несколько месяцев, но в еду пригодны и молодые плоды с тонкой кожурой и несформировавшимися семенами."},{"title":"Условия выращивания паттисона","content":"Патиссоны хорошо растут и плодоносят в условиях длинного дня, но по своей природе является культурой короткого дня."},{"title":"Посадка паттисона","content":"Семена высевают в открытый грунт при температуре почвы не менее +12-14°С, в конце мая. Способ посева квадратно-гнездовой, минимальная схема сильнорослых сортов 70х90 см. Лунки перед посевом предварительно поливают. В каждую лунку кладут по 3—4 семени на глубину 3-5 см, затем их засыпают сухим перегноем или торфом. Дополнительно необходимо продумать защиту лунок с семенами от ворон и грачей с помощью домиков из бутылок и палок."},{"title":"Вредители и болезни паттисона","content":"те же что и у кабачка"},{"title":"Полезные свойства паттисона","content":"Патиссон прекрасно усваивается организмом, они более чем на 80% состоит из структурированной воды. К тому же их плоды низкокалорийные - 27 19 кКал/100 г соответственно, что делает их весьма полезным диетическим продуктом. Помимо белков, жиров и углеводов, кабачки и патиссоны содержат органические, насыщенные и ненасыщенные жирные кислоты и сахара. Молодые плоды содержат витамины группы В, А, Е, С, РР, бета-каротин, микроэлементы: железо, кальций, магний, натрий, калий и фосфор. При этом желтоокрашенные плоды содержат больше витамина Е и С, а так же каротин."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Перец (Capsicum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"теплица, оранжерея","eating":"плоды"}'::jsonb, '[{"title":"Виды и сорта перца","content":"Перец овощной (Capsicum annuum)"},{"title":"Уход за перцем","content":"Свето- и теплолюбивая культура — оптимальная температура прорастания семян — +20…+25 °С, для развития — +18…+25 °С. Не растет при температуре ниже +5 °С. Кроме того, перец плохо переносит перепады ночных и дневных температур."},{"title":"Посадка перца","content":"Семена нужно подготовить к посеву, сначала их обеззараживают в растворе марганцовки, после этого промывают и замачивают в растворе жидкого гумата натрия."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Петрушка (Petroselínum)','4', '{"kind":"двулетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимостойкое","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"листья, побеги, корни"}'::jsonb, '[{"title":"Виды и сорта петрушки","content":"В розетке корневой петрушки от 15 до 40 листьев, у листовой — от 10 до 100."},{"title":"Уход за петрушкой","content":"Культура длинного дня, поэтому наибольший прирост зеленой массы и формирование цветоноса наблюдается в летний период. При выращивании зимой в домашних условиях растениям петрушки необходима досветка в течении 3-5 часов."},{"title":"Посадка петрушки","content":"Семена прорастают при +2...+3 градусах. Для получения корнеплодов семена высеивают в конце апреля–в начале мая."},{"title":"Уход за петрушкой","content":"Листовая петрушка готова к выборочной уборке через 40-50 дней после всходов и срезаются весь дачный сезон. Полноценные корнеплоды обычно вырастают через 60-80 дней, убирают их до наступления заморозков и хранят в холодильнике."},{"title":"Полезные свойства петрушки","content":"Растения содержат витамины В1, В2, С, К, каротин, минеральные соли калия, кальция и железа, а так же инулин, белки и эфирные масла. Большое количество эфирных масел содержится в семенах кудрявой петрушки."},{"title":"Болезни и вредители петрушки","content":"Из болезней на петрушке чаще всего встречаются альтернариоз, ризоктониоз и мокрая бактериальная гниль."},{"title":"Петрушка вне ЗАКОНА!","content":"В 2011 году Роспотребнадзор расширил список растений, содержащих наркотические и ядовитые вещества, в новый «черный» список вошла и петрушка курчавая (Рetroselinum crispuma), а точнее, ее плоды. По мнению специалистов Роспотребнадзора из нее можно выделить опасные наркотические вещества. Однако, «облавы» на дачников, выращивающих петрушку, пока не планируются…"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Редис, редиска (Raphanus sativus)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое, умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"корни"}'::jsonb, '[{"title":"Виды и сорта редиски","content":"Редис — группа разновидностей от вида Редька посевная (Raphanus sativus). Выведен в ХVI веке."},{"title":"Уход за редисом","content":"Редис - холодостойкое растение, оптимальная температура развития +16…+20 °С. При более высокой температуре, особенно в парнике и теплице, за короткий срок листовая розетка вытягивается и набирает зеленую массу. В этом случае есть риск получить меньше урожая на 30-40%."},{"title":"Посадка редиса","content":"Редис — растение короткого дня, поэтому семена желательно высеивать в ранние сроки — с 15-18 апреля по 25 мая."},{"title":"Вредители и болезни редиса","content":"Капустная тля"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Салат (Lactuca)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое, умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"листья"}'::jsonb, '[{"title":"Уход за салатом","content":"Салат — холодостойкое растение: молодые растения переносят небольшие заморозки."},{"title":"Посадка салата","content":"Лучшие предшественники для салата - это капуста, томат, перец, картофель, под которые ранее вносили органические удобрения."},{"title":"Болезни и вредители салата","content":"Бактериоз"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Свекла (Beta)','4', '{"kind":"двулетник, однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимостойкое","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"листья, корни"}'::jsonb, '[{"title":"Виды и сорта свеклы","content":"Родина — берега Средиземноморья."},{"title":"Уход за свеклой","content":"Свекла — простое в уходе огородное растение, которому требуется только своевременное прореживание (растения необходимо сажать на расстоянии 15-20 см друг от друга), прополка и полив."},{"title":"Уборка свеклы","content":"Свеклу убирают во второй половине сентября, она чувствительна к понижениям температуры. Свеклу подкапывают вилами, очищают от земли и срезают ботву. На хранение свеклу складывают после того как задубеют срезы. Оптимальная температура хранения — +2…+4 °С при влажности 80-85 %."},{"title":"Болезни и вредители свеклы","content":"Из химических препаратов на свекле рекомендуется: против свекловичных блошек, тлей, клопов, цикадок БИ-58 0,05-0,08 мл/м2, против гусениц листогрызущих совок – Сумитион 0,06-0,1 мл/м2, против минирующей мухи сосущих вредителей – Карбофос 60 г/10 л воды."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Сельдерей (Apium)','4', '{"kind":"двулетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое, умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"листья, побеги, корни"}'::jsonb, '[{"title":"Виды и сорта сельдерея","content":"Листовая форма сельдерея - наиболее близкая к дикому сельдерею: на растении образуется множество очень ароматных листьев с полыми черешками. Корни у таких растений сильно разветвленные, грубые и несъедобные."},{"title":"Условия выращивания для сельдерея","content":"Сельдерей - культура короткого дня. Растения сельдерея, выращенные при коротком дне в конце января – февраля, имеют хороший внешний вид и не образуют цветоносов в первый год."},{"title":"Посадка сельдерея","content":"В Средней полосе выращивание корневого сельдерея возможно только через рассаду. Посев семян целесообразно проводить с середины января до середины марта. Листовые сорта так же желательно выращивать через рассаду, посев семян в феврале-марте, но возможен и посев в открытый грунт в грядки, как только прогреется земля (конец апреля - начало мая)."},{"title":"Сорта сельдерея, рекомендованные Агрофирмой ПОИСК:","content":"Силач - Среднеспелый сорт корневого сельдерея. Период от полных всходов до технической спелости 140-150 дней. Листовая пластина насыщенно зеленого цвета, черешки светло-зеленые. Корнеплод округлой формы диаметром 10-12 см. Окраска поверхности желтоватая, мякоть цвета слоновой кости, нежная и сочная. Масса корнеплода 300-600 г. Сорт отличается устойчивостью к цветушности, характеризуется высокой урожайностью, ароматичностью и повышенным содержанием сахаров."},{"title":"Вредители и болезни сельдерея","content":"Из болезней на сельдерее встречаются: септориоз (белая пятнистость) – поражает листья, черешки и стебли, церкоспороз - поражает все надземные органы растения."},{"title":"Полезные свойства сельдерея","content":"Сельдерей – ценная овощная культура, предоставляюща организму человека большое количество витаминов: С, В1, В2, В6, Е, К и РР. Кроме того, в сельдерее отмечено высокое содержание сахаров, органических кислот, солей калия, железа, кальция, фосфора, натрия, магния, и целый ряд ценных аминокислот. Все части растения богаты эфирными маслами, которые придают ему специфический вкус."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Тимьян, чабрец (Thymus)','4', '{"kind":"многолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"засухоустойчивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимует с укрытием","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"цветы, листья, побеги"}'::jsonb, '[{"title":"Выращивание тимьяна","content":"Тимьян предпочитает солнечные участки, известковые и сухие почвы. Полив очень ограничен, поскольку тимьян – растение сухих регионов. Весной вносится компост. Посев весной, в рядки на расстоянии 25 см, хорошо всходит. Размножение делением, усами и черенками. Культура проста в уходе. Тимьян можно выращивать в кадках, горшках и вазонах."},{"title":"Виды и сорта тимьяна","content":"При озеленении участка можно использовать лимонный тимьян (Thymus citriodorum), который похож на тимьян обыкновенный, но с характерным лимонным ароматом, он неприхотлив к почвам и климату, но зимой нуждается в укрытии и защите."},{"title":"","content":"Применение тимьяна в медицине"},{"title":"Применение тимьяна в медицине","content":"Эфирное масло тимьяна обладает сильным антисептическим действием. В народной медицине тимьян используют при коклюше, заболевании бронхов, ревматизме, бессоннице, радикулитах, невралгии, а также как успокаивающее средство при расстройствах нервной системы."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Томат, помидор (Solanum lycopersicum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды"}'::jsonb, '[{"title":"Выбор сортов томатов","content":"По продолжительности вегетационного периода томаты подразделяются на раннеспелые (85-100 дней), среднеранние (101-110 дней), среднеспелые (111-115 дней), среднепоздние (116-120 дней) и поздние (более 120 дней). При выборе сорта помните прежде всего о том, что, как правило, чем сорт более скороспелый, тем ниже качество его плодов. Наиболее крупноплодные сорта с самыми ценными плодами более капризны и созревают позже."},{"title":"Выращивание рассады томатов","content":"Как подготовить семяна помидор к посадке"},{"title":"Парниковый метод выращивания томатов","content":"Высадка рассады помидор в теплицу"},{"title":"Выращивание томатов в открытом грунте","content":"Сроки высадки томатов в грунт очень ограниченны и различны не только в разных регионах нашей страны, но даже в разных областях. В открытый грунт томаты обычно высаживают между 6 и 11 июня. В случае более поздних заморозков нужно своевременно прослушать прогноз погоды и укрыть на ночь томаты пленкой или лутрасилом. Впрочем, сама я ни разу этого не делала — моя прекрасно закаленная рассада не раз переносила легкие заморозки без всяких укрытий."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Укроп, Анетум (Anethum)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"цветы, листья"}'::jsonb, '[{"title":"Виды и сорта укропа","content":"Монотипный род. Родина — Средиземноморье, Юго-западная и Центральная Азия."},{"title":"Уход за укропом","content":"Укроп - холодостойкое растение, выдерживающее заморозки. Может расти при температуре +8…+10 градусов. Семена начинают прорастать уже при температуре +3 °С, хотя и медленно, всходы при таком режиме появится не раньше чем через 20 дней. Быстрее всего всходы укропа появляются при температуре +20...+22 °С – на 4-5 сутки - замоченные семена, а сухие обычно через 10-14 суток. Оптимальная температура для роста культуры +18...+20 °С. Для цветения и созревания семян температура должна быть более высокой."},{"title":"Посадка укропа","content":"Лучшие предшественники укропа — огурцы, капуста, свекла, бобовые культуры. Не подходят места, где в прошлом году росли сельдерейные, например морковь."},{"title":"Полезные свойства укропа","content":"Укроп содержит разнообразные витамины, флаваноиды, никотиновую и фолиевую кислоту, каротин, пектиновые вещества, эфирные масла, а также соли железа, кальция, калия, фосфора в легкоусвояемой форме."},{"title":"Болезни и вредители укропа","content":"Мучнистая роса"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Физалис (Physalis)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"теневыносливое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"плоды"}'::jsonb, '[{"title":"Виды и сорта физалиса","content":"Более ста его видов распространено в тропических и субтропических странах Центральной Америки, отдельные виды встречаются в юго-восточной Африке. В Европу и Азию физалис завезен в XVII веке. В Россию он попал одновременно с томатом."},{"title":"Уход за физалисом","content":"Физалис — растение короткодневное, теневыносливое, хорошо растет на любых почвах."},{"title":"Уборка урожая физалиса","content":"Плоды физалиса созревают не одновременно, уборку урожая необходимо проводить регулярно. Плоды овощного (мексиканского) физалиса можно дозаривать."},{"title":"Посадка физалиса","content":"Выращивают рассадным способом или высевая непосредственно в грунт. Высадку в грунт осуществляют после последних заморозков."},{"title":"","content":"Применение физалиса в кулинарии"}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Цуккини (Cucurbita)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"не зимует","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт, теплица, оранжерея","eating":"плоды"}'::jsonb, '[{"title":"Уход за цуккини","content":"Цуккини — однолетнее тепло-, свето- и влаголюбивое растение, предпочитающее плодородные почвы с нейтральной реакцией среды. Уход за цуккини такой же, как и за белоплодными кабачками: полив, удобрение, рыхление почвы и уничтожение сорняков."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Чеснок (Allium)','4', '{"kind":"однолетник","recommend_position":"","regard_to_light":"теневыносливое, светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимостойкое","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"побеги, луковицы"}'::jsonb, '[{"title":"Виды и сорта чеснока","content":"Чеснок известен в культуре более 4 тысяч лет. Это растение родом из Центральной и Юго-Западной Азии, в частности из Афганистана. В России чеснок распространился в XII — XIII веке."},{"title":"Уход за чесноком","content":"Чеснок — холодостойкое растение, требовательное к плодородию почвы и влаге, особенно в первые недели после высадки. Оптимальная температура для роста и развития чеснока в начальный период вегетации +5…+10 °С, в период формирования зубков +15…+20 °С, в период созревания +20…+25 °С."},{"title":"Посадка чеснока","content":"Озимые сорта высаживают осенью, в основном в районах с благоприятными для перезимовки условиями. Яровой чеснок высаживают весной практически повсеместно."},{"title":"Уборка урожая чеснока","content":"Озимые сорта созревают в конце июля–первой декаде августа. Выдернутые головки чеснока необходимо хорошо просушить. При обрезке оставляют длину шейки около 5 см."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Шалфей, сальвия (Salvia )','4', '{"kind":"многолетник","recommend_position":"","regard_to_light":"светолюбивое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимостойкое","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"цветы, листья, побеги, корни"}'::jsonb, '[{"title":"Виды и сорта шалфея","content":"В роду насчитывается до 700 видов шалфея, около 80 из них может произрастать на нашей территории, но лишь несколько активно используются в культуре. Среди них популярное пряноароматическое растение шалфей лекарственный."},{"title":"","content":"Выращивание шалфея"},{"title":"Выращивание шалфея","content":"Шалфей предпочитает солнечные участки, проницаемые известковые почвы. Полив по мере необходимости, ежегодно после срезки урожая, производить подкормку жидким удобрением. Зимой шалфею требуется защита от мороза."},{"title":"Размножение шалфея","content":"В основном растение размножают рассадой. Посев проводят в феврале–марте в отапливаемом помещении. Появившиеся всходы пикируют в ящики. Вторую пикировку проводят в апреле–мае. Необходим умеренный полив, подкормка азотными удобрениями. Возможно размножение отводками."},{"title":"Применение шалфея в медицине","content":"Издавна листья шалфея использовали для лечения многих заболеваний. Вообще, в древности, шалфей называли «травой жизни». Экстракту приписывали животворящие свойства. Растение считалось превосходным средством для изготовления магической палочки, амулетов и оберегов."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
INSERT INTO reference.plants (title, category_id, short_info, notes, img_links, created_at, creator) VALUES('Щавель (Rúmex acetósa)','4', '{"kind":"многолетник","recommend_position":"","regard_to_light":"теневыносливое","regard_to_moisture":"умеренно влаголюбивое","flowering_time":"","hight":"","classifiers":"","ground":"","wintering":"зимостойкое","decorativeness":"","composition":"","shearing":"","growing":"открытый грунт","eating":"листья"}'::jsonb, '[{"title":"Виды и сорта щавеля","content":"В России (в основном в Европейской части) насчитывается около 70 ви­дов растения. В диком виде щавель произрастает в Евразии, Африке, Северной и Южной Америке. В России растение распространено повсеместно в европейской части (за исключением Крыма), на Кавказе, в Сибири, на Дальнем Востоке."},{"title":"Уход за щавелем","content":"Растение холодостойкое, хорошо зимует без укрытия и очень рано отрастает весной."},{"title":"Посадка щавеля","content":"Лучшие предшественники щавеля — корнеплодные, тыквенные и зеленые культуры: огурцы, томаты, капуста, морковь, свекла, редис, ранний картофель."},{"title":"Полезные свойства щавеля","content":"В листьях щавеля обыкновенного или щавеля кислого содержится белок, до 145 мг% аскорбиновой кислоты, витамины А, Вр B2, РР, дубильные вещества, флавоноиды, органические кислоты, сапонины, большое количество щавелекислого кальция, разнообразные минеральные соли, особенно много железа и калия."},{"title":"Применение щавеля в кулинарии и медицине","content":"Щавель способствует лучшему пищеварению. В народ­ной медицине отвар корней и семян используется в качестве вяжущего средства, а отвар корней — при бо­лях в пояснице, ревматизме, при гастрите, отравлениях, в каче­стве мочегонного и потогонного средства, в больших дозах — в качестве слабительного и противоглистного. Траву в виде отвара используют при мочекаменной болезни."}]'::jsonb, '[]'::jsonb, CURRENT_TIMESTAMP, CURRENT_USER);
